# mainimport requestsSITES = {    "GitHub": "https://github.com/{}",    "Twitter": "https://twitter.com/{}",    "Instagram": "https://www.instagram.com/{}",    "Reddit": "https://www.reddit.com/user/{}",    "Youtube": "https://www.youtube.com/{}",    "TikTok": "https://www.tiktok.com/@{}"}def scan(username):    results = {}    for site, url in SITES.items():        try:            r = requests.get(url.format(username), timeout=5)            results[site] = (r.status_code == 200)        except requests.RequestException:            results[site] = None    return resultsif __name__ == "__main__":    user = input("Nickname: ")    print("\nSearching for username:", user)    print("-" * 50)    res = scan(user)for site, found in res.items():    if found:        print(f"{site}: {SITES[site].format(user)}")    else:        print(f"{site}: NOT FOUND")