# mainimport requestsSITES = {    "GitHub": "https://github.com/{}",    "Twitter": "https://twitter.com/{}",    "Instagram": "https://www.instagram.com/{}",    "Reddit": "https://www.reddit.com/user/{}"}def scan(username):    results = {}    for site, url in SITES.items():        try:            r = requests.get(url.format(username), timeout=5)            results[site] = (r.status_code == 200)        except requests.RequestException:            results[site] = None    return resultsif __name__ == "__main__":    user = input("Nickname: ")    res = scan(user)for site, found in res.items():    print(f"{site}: {'FOUND' if found else 'NOT FOUND'}")